import React, { useState } from "react";

const ThingsWeAreGoodAt = () => {
  const [activeTab, setActiveTab] = useState("home");

  return (
    <div className="flex justify-center items-center flex-col w-full max-w-[1300px] mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Things We Are Good at</h1>

      {/* Mobile select dropdown */}
      <select
        className="sm:hidden border px-3 py-2 rounded-md mb-4"
        value={activeTab}
        onChange={(e) => setActiveTab(e.target.value)}
      >
        <option value="home">Home</option>
        <option value="profile">Profile</option>
        <option value="messages">Messages</option>
      </select>

      {/* Desktop tab buttons */}
      <div className="hidden sm:flex border-b w-full justify-center gap-4 mb-4">
        {["home", "profile", "messages"].map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`py-2 px-4 border-b-2 font-medium transition-all ${
              activeTab === tab
                ? "border-blue-500 text-blue-600"
                : "border-transparent text-gray-600 hover:text-blue-500"
            }`}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Tab content */}
      <div className="text-gray-700 text-center max-w-xl">
        {activeTab === "home" && (
          <p>
            Welcome to the <strong>Home tab!</strong> Explore the latest updates
            and news here.
          </p>
        )}
        {activeTab === "profile" && (
          <p>
            This is your <strong>Profile</strong> tab, where you can update your
            personal info and manage account details.
          </p>
        )}
        {activeTab === "messages" && (
          <p>
            <strong>Messages:</strong> View your recent messages, chat with
            friends, and manage conversations.
          </p>
        )}
      </div>
    </div>
  );
};

export default ThingsWeAreGoodAt;
