function HomeCarousel() {
  return (
    <div className="relative h-[400px]">
      <Carousel
        useKeyboardArrows={true}
        showThumbs={false}
        infiniteLoop
        autoPlay
        showStatus={false}
      >
        {images.map((item, index) => (
          <div key={index} className="relative h-[400px]">
            <img
              src={item.url}
              alt={item.title}
              className="object-cover w-full h-[400px]"
            />
            <div className="absolute inset-0 bg-black/50 flex flex-col justify-center items-center px-6 text-center text-white h-[400px]">
              <h2 className="text-2xl md:text-4xl font-bold mb-3">
                {item.title}
              </h2>
              <p className="mb-5 text-sm md:text-base max-w-xl">
                {item.description}
              </p>
              <a
                href={item.buttonLink}
                className="bg-blue-500 hover:bg-blue-600 transition px-5 py-2 rounded-full text-white font-medium"
              >
                Learn More
              </a>
            </div>
          </div>
        ))}
      </Carousel>
    </div>
  );
}
