import React, { useState } from "react";

const ThingsWeAreGoodAt = () => {
  const [activeTab, setActiveTab] = useState("Digital Strategy");

  const tabs = [
    "Digital Strategy",
    "Design",
    "Web",
    "Mobile",
    "Social Media",
    "SEO",
    "Content Writing",
  ];

  return (
    <div className="flex justify-center items-center flex-col w-full max-w-[1300px] mx-auto p-4">
      <h1 className="text-2xl font-bold mb-6">Things We Are Good at</h1>

      {/* Mobile select dropdown */}
      <select
        className="sm:hidden border px-3 py-2 rounded-md mb-4"
        value={activeTab}
        onChange={(e) => setActiveTab(e.target.value)}
      >
        {tabs.map((tab) => (
          <option key={tab} value={tab}>
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </option>
        ))}
      </select>

      {/* Desktop tab buttons */}
      <div className="hidden sm:flex border-b w-full justify-center gap-4 mb-4">
        {tabs.map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`py-2 px-4 border-b-2 font-medium transition-all ${
              activeTab === tab
                ? "border-blue-500 text-blue-600"
                : "border-transparent text-gray-600 hover:text-blue-500"
            }`}
          >
            {tab.charAt(0).toUpperCase() + tab.slice(1)}
          </button>
        ))}
      </div>

      {/* Tab content */}
      <div className="text-gray-700 text-center max-w-xl min-h-[100px]">
        {activeTab === "Digital Strategy" && (
          <p>
            Welcome to the <strong>Digital Strategy tab!</strong> We provide our
            phenomenal services at a global level. We foster brands so that they
            can evolve into something massive. We strive to make marketing easy
            and convenient for you that your product gets the recognition it
            deserves. We provide you with a marketing strategy that will prove
            to be only beneficial for you and your brand.
          </p>
        )}
        {activeTab === "Design" && (
          <p>
            We establish brands that are fascinating and wholly desirable.
            Branding is about establishing the companyâ€™s personality and
            experience that reverberates with customers and ensures their
            satisfaction. A good brand is one that, through its product, can
            attract customers on a large scale. In comparison, a good digital
            company like us is the one that contemplates the needs of these
            brands and brings their imaginations into life. Visualizers and
            technologists at Buraqsys have helped brands all over the world in
            making world records.
          </p>
        )}
        {activeTab === "Web" && (
          <p>
            <strong>Web:</strong> We are here to develop websites and website
            content for mobile phones and tablets, desktop screens, and laptops.
            Our expertise is all over the place, which is an exciting feature
            for the brands to be fascinated and impressed.
          </p>
        )}
        {activeTab === "Mobile" && (
          <p>
            <strong>Mobile:</strong> Now that technology has advanced to a
            shocking level, and we try exploiting it as much as possible. So we
            create mobile apps with effective user interfaces that provide an
            intuitive and gratifying experience. No matter what your aim is, we
            have got your back.
          </p>
        )}
        {activeTab === "Social Media" && (
          <p>
            <strong>Social Media:</strong> View your recent Social Media, chat
            with friends, and manage conversations.
          </p>
        )}
        {activeTab === "SE" && (
          <p>
            <strong>Web:</strong> View your recent Web, chat with friends, and
            manage conversations.
          </p>
        )}
      </div>

      {/* Dot Indicators */}
      <div className="flex gap-2 mt-6">
        {tabs.map((tab) => (
          <span
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`w-3 h-3 rounded-full cursor-pointer transition-all duration-200 ${
              activeTab === tab ? "bg-blue-600 scale-110" : "bg-gray-300"
            }`}
          />
        ))}
      </div>
    </div>
  );
};

export default ThingsWeAreGoodAt;
