import React from "react";

const ClientCard = ({ image, name }) => {
  return (
    <div className="group bg-white shadow-lg items-center overflow-hidden max-w-sm w-full transition-transform duration-300 hover:-translate-y-2 hover:shadow-2xl">
      {/* Image */}
      <div className="w-full h-60 overflow-hidden relative group">
        <img
          src={image}
          alt={name}
          className="w-full h-full w-[50px] h-[50px] object-cover transition-transform duration-300 group-hover:scale-105"
        />

        {/* Bottom-to-top shadow on hover */}
        <div className="absolute inset-0 bg-gradient-to-t from-black/50 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
      </div>
    </div>
  );
};

export default ClientCard;
