import React, { useState } from "react";
import digital from "../../assests/marketing-icon.webp";
import design_icon from "../../assests/design-icon.webp";
import web_icon from "../../assests/web-icon.webp";
import mobile_icon from "../../assests/mobile-icon.webp";
import social_icon from "../../assests/social-icon.webp";
import seo_icon from "../../assests/seo-icon.webp";
import content_icon from "../../assests/content-icon.webp";

const ThingsWeAreGoodAt = () => {
  const [activeTab, setActiveTab] = useState("Digital Strategy");

  const tabs = [
    "Digital Strategy",
    "Design",
    "Web",
    "Mobile",
    "Social Media",
    "SEO",
    "Content Writing",
  ];

  // Tab content function to avoid repetition
  const renderContent = (tab) => {
    switch (tab) {
      case "Digital Strategy":
        return (
          <div className="flex flex-col">
            <strong>Digital Strategy</strong>
            <div className="flex">
              <img src={digital} alt="digital_strategy" />
              <p className="flex jusitfy-left w-1/2">
                We provide our phenomenal services at a global level. We foster
                brands so that they can evolve into something massive. We strive
                to make marketing easy and convenient for you that your product
                gets the recognition it deserves. We provide you with a
                marketing strategy that will prove to be only beneficial for you
                and your brand.
              </p>
            </div>
          </div>
        );
      case "Design":
        return (
          <div className="flex flex-col">
            <strong>Design</strong>
            <div className="flex">
              <img src={design_icon} alt="design" />
              <p className="flex jusitfy-left w-1/2">
                We establish brands that are fascinating and wholly desirable.
                Branding is about establishing the companyâ€™s personality and
                experience that reverberates with customers and ensures their
                satisfaction. A good brand is one that, through its product, can
                attract customers on a large scale. In comparison, a good
                digital company like us is the one that contemplates the needs
                of these brands and brings their imaginations into life.
                Visualizers and technologists at Buraqsys have helped brands all
                over the world in making world records.
              </p>
            </div>
          </div>
        );
      case "Web":
        return (
          <div className="flex flex-col">
            <img src={web_icon} alt="web" />
            <div className="flex">
              <strong className="w-1/2">Web:</strong>
              <p className="flex jusitfy-left w-1/2">
                <strong>Web:</strong> We provide you with a web strategy that
                will make your brand stand out from the crowd.
              </p>
            </div>
          </div>
        );
      case "Mobile":
        return (
          <div className="flex flex-col">
            <img src={mobile_icon} alt="mobile" />
            <div className="flex">
              <strong className="w-1/2">Mobile:</strong>
              <p className="flex jusitfy-left w-1/2">
                We are here to develop websites and website content for mobile
                phones and tablets, desktop screens, and laptops. Our expertise
                is all over the place, which is an exciting feature for the
                brands to be fascinated and impressed.
              </p>
            </div>
          </div>
        );
      case "Social Media":
        return (
          <div className="flex flex-col">
            <div className="flex">
              <strong className="w-1/2">Social Media:</strong>
            <img src={social_icon} alt="social media" />

              <p className="flex jusitfy-left w-1/2">
                We provide you with a social media strategy that will make your
                brand stand out from the crowd.
              </p>
            </div>
          </div>
        );
      case "SEO":
        return (
          <div className="flex flex-col">
            <strong>SEO:</strong>
            <div className="flex gap-5">
              <img src={seo_icon} alt="SEO" />
              <p className="flex jusitfy-left w-1/2">
                We optimize your website and make it accessible to the world. We
                help you to rank your website on the first page of Google.
              </p>
            </div>
          </div>
        );
      case "Content Writing":
        return (
          <div className="flex flex-col">
            <strong className="w-1/2">Content Writing:</strong>

            <div className="flex gap-5">
              <img src={content_icon} alt="content writing" />
              <p className="flex jusitfy-left w-1/2">
                We provide you with quality content that will make your audience
                fall in love with your brand.
              </p>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="flex justify-center items-center flex-col w-full max-w-[1300px] mx-auto p-4 shadow-[0px_5px_15px_rgba(0,0,0,0.35)]">
      <h1 className="text-[72px] py-5 mb-6">Things We Are Good at</h1>

      {/* Mobile select dropdown */}
      <select
        className="sm:hidden border px-3 py-2 rounded-md mb-4"
        value={activeTab}
        onChange={(e) => setActiveTab(e.target.value)}
      >
        {tabs.map((tab) => (
          <option key={tab} value={tab}>
            {tab}
          </option>
        ))}
      </select>

      {/* First tab content display (after tabs) */}
      {/* <div className="text-gray-700 text-center max-w-xl min-h-[100px] mb-6">
        {renderContent(activeTab)}
      </div> */}

      {/* Dot Indicators */}
      <div className="flex gap-2 mt-2 mb-6">
        {tabs.map((tab) => (
          <span
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`w-3 h-3 rounded-full cursor-pointer transition-all duration-200 ${
              activeTab === tab ? "bg-blue-600 scale-110" : "bg-gray-300"
            }`}
          />
        ))}
      </div>

      {/* Second tab content display (after indicators) */}
      <div className="py-10 text-gray-700 text-center max-w-xl min-h-[100px]">
        {renderContent(activeTab)}
      </div>
      {/* Desktop tab buttons */}
      <div className="hidden sm:flex border-b w-full justify-center gap-1 mb-6">
        {tabs.map((tab) => (
          <button
            key={tab}
            onClick={() => setActiveTab(tab)}
            className={`py-[14px] px-[20px] font-medium transition-all ${
              activeTab === tab
                ? "border-blue-500 bg-[#F8F8F8] text-[#666666]"
                : "border-transparent bg-[#125D93] text-white hover:text-blue-500"
            }`}
          >
            {tab}
          </button>
        ))}
      </div>
    </div>
  );
};

export default ThingsWeAreGoodAt;
