import React, { useState } from "react";
import SwipeableViews from "react-swipeable-views";
import { Box, Typography, IconButton, Grid } from "@mui/material";
import ArrowBackIosNewIcon from "@mui/icons-material/ArrowBackIosNew";
import ArrowForwardIosIcon from "@mui/icons-material/ArrowForwardIos";
import Card from "../cart/card";
import frontend from "../../assests/frontend-developer.jpg";
import ceo from "../../assests/CEO.jpg";
import marketing_manager from "../../assests/Marketing-Manager.jpg";

const teams = [
  {
    image: ceo,
    name: "Muhammad Bilal",
    description: "CO Founder & CEO",
  },
  {
    image: marketing_manager,
    name: "Iqra bilal",
    description: "Social media Marketing Manager",
  },
  {
    image: ceo,
    name: "Muhammad Hassan",
    description: "Senior Graphic designer",
  },
  {
    image: frontend,
    name: "Ali Ahmad",
    description: "Senior Frontend developer",
  },
  {
    image: ceo,
    name: "Sara Khan",
    description: "Project Manager",
  },
  {
    image: marketing_manager,
    name: "Zain Raza",
    description: "UI/UX Designer",
  },
];

const Team = () => {
  const cardsPerSlide = 3;
  const totalSlides = Math.ceil(teams.length / cardsPerSlide);
  const [activeStep, setActiveStep] = useState(0);

  const handleNext = () => {
    if (activeStep < totalSlides - 1) setActiveStep((prev) => prev + 1);
  };

  const handleBack = () => {
    if (activeStep > 0) setActiveStep((prev) => prev - 1);
  };

  const getSlides = () => {
    const slides = [];
    for (let i = 0; i < teams.length; i += cardsPerSlide) {
      slides.push(teams.slice(i, i + cardsPerSlide));
    }
    return slides;
  };

  const slides = getSlides();

  return (
    <Box className="flex justify-center items-center w-full bg-white py-10">
      <Box className="flex flex-col px-[30px] max-w-[1330px] w-full text-center mx-auto">
        <Box mb={4}>
          <Typography
            variant="body2"
            sx={{
              color: "#3B82F6",
              textTransform: "uppercase",
              fontWeight: 600,
            }}
          >
            Meet the leaders
          </Typography>
          <Typography variant="h4" fontWeight="bold" mt={1}>
            The Best Team
          </Typography>
          <Typography mt={2} color="text.secondary">
            With our individualities, we share a common interest in the digital
            world and join hands to build a better virtual world of yours where
            you can stand out. Let us introduce the creative minds behind{" "}
            <strong>BURAQSYS Studio</strong>.
          </Typography>
        </Box>

        <Box position="relative">
          <IconButton
            onClick={handleBack}
            disabled={activeStep === 0}
            sx={{
              position: "absolute",
              top: "50%",
              left: "-40px",
              transform: "translateY(-50%)",
              zIndex: 2,
              background: "#f0f0f0",
            }}
          >
            <ArrowBackIosNewIcon />
          </IconButton>

          <SwipeableViews
            index={activeStep}
            onChangeIndex={setActiveStep}
            enableMouseEvents
          >
            {slides.map((group, index) => (
              <Grid container spacing={3} key={index} justifyContent="center">
                {group.map((item, idx) => (
                  <Grid item xs={12} sm={6} md={4} key={idx}>
                    <Card
                      icon={item?.image}
                      title={item?.name}
                      description={item?.description}
                    />
                  </Grid>
                ))}
              </Grid>
            ))}
          </SwipeableViews>

          <IconButton
            onClick={handleNext}
            disabled={activeStep === totalSlides - 1}
            sx={{
              position: "absolute",
              top: "50%",
              right: "-40px",
              transform: "translateY(-50%)",
              zIndex: 2,
              background: "#f0f0f0",
            }}
          >
            <ArrowForwardIosIcon />
          </IconButton>
        </Box>
      </Box>
    </Box>
  );
};

export default Team;
