import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "../../App.css";

const images = [
  {
    url: "https://res.cloudinary.com/demo/image/upload/v1652345767/docs/demo_image2.jpg",
    title: "Web Development",
    description: "Discover our most vibrant and successful campaigns yet.",
    buttonLink: "#",
  },
  {
    url: "https://res.cloudinary.com/demo/image/upload/v1652366604/docs/demo_image5.jpg",
    title: "Mobile App Development",
    description: "We build Advanced & ",
    buttonLink: "#",
  },
  {
    url: "https://res.cloudinary.com/demo/image/upload/v1652345874/docs/demo_image1.jpg",
    title: "Social Media Marketing",
    description: "We reach people Not just their browsers",
    buttonLink: "#",
  },
];

function HomeCarousel() {
  return (
    <div className="relative">
      <Carousel
        useKeyboardArrows={true}
        showThumbs={false}
        infiniteLoop
        autoPlay
      >
        {images.map((item, index) => (
          <div key={index} className="relative">
            <img
              src={item.url}
              alt={item.title}
              className="object-cover w-full h-[600px]"
            />
            <div className="absolute inset-0 bg-black/50 flex flex-col justify-center items-start px-10 md:px-20 text-left text-white">
              <h2 className="text-3xl md:text-5xl font-bold mb-4">
                {item.title}
              </h2>
              <p className="mb-6 text-base md:text-lg max-w-xl">
                {item.description}
              </p>
              <a
                href={item.buttonLink}
                className="bg-blue-500 hover:bg-blue-600 transition-colors px-6 py-3 rounded-full text-white font-semibold"
              >
                View Details
              </a>
            </div>
          </div>
        ))}
      </Carousel>
    </div>
  );
}

export default HomeCarousel;
